let usuario = [{imagem:"assets/img/catanacomics.svg", texto_strong:"catanacomics",
                     texto_span:"Catana"}]

export default function Usuario() {

    // let usuario = [{imagem:"assets/img/catanacomics.svg", texto_strong:"catanacomics",
    //                  texto_span:"Catana"}]

    return (
        <div>
        {
            usuario.map((usr) => {
                return <DadosUsuario imagem={usr.imagem} texto_strong={usr.texto_strong} texto_span={usr.texto_span} />
            })
        }
        </div>
    )
}

function DadosUsuario(props) {
    return (
        <div class="usuario">
            <img src={props.imagem} />
            <div class="texto">
                <strong>{props.texto_strong}</strong>
                <span>
                {props.texto_span}
                <ion-icon onClick={nomePrompt} name="pencil"></ion-icon>
                </span>
            </div>
        </div>
    )
}

function nomePrompt() {
    console.log(usuario)
    usuario.texto_span = prompt('Novo nome de usuário: ')
    location.reload()
}
